import OpenAI from "openai";
import type { ChatCompletionMessageParam } from "openai/resources/chat/completions";

// the newest OpenAI model is "gpt-5" which was released August 7, 2025. do not change this unless explicitly requested by the user
const openai = new OpenAI({ 
  apiKey: process.env.OPENAI_API_KEY || process.env.OPENAI_API_KEY_ENV_VAR || "default_key"
});

export interface TranscriptionResponse {
  text: string;
  speakerName: string;
  speakerInitials: string;
  speakerColor: string;
}

export interface ActionItemResponse {
  title: string;
  description: string;
  assignedTo: string;
  dueDate: string;
}

export interface InsightResponse {
  keyTopics: string[];
  sentiment: string;
  sentimentScore: string;
  summary: string;
  nextSteps: string;
}

export class OpenAIService {
  private conversationHistory: Map<string, ChatCompletionMessageParam[]> = new Map();
  private readonly systemPrompt = `You are MeetingFlow, an AI assistant specialized in transcribing meetings for newsletter publishers and content creators. You help extract actionable insights, identify speakers, and highlight important action items.

Your responses should be:
- Professional but conversational
- Focused on content strategy and newsletter publishing
- Helpful in identifying actionable tasks
- Natural and human-like, avoiding robotic language
- Contextually aware of previous conversation

When processing meeting content, focus on:
- Content strategy discussions
- Editorial calendar planning
- Audience engagement metrics
- Campaign performance reviews
- Team assignments and deadlines
- Tool integrations and workflows`;

  async processTranscription(
    meetingId: string, 
    audioText: string, 
    speakerContext?: string
  ): Promise<TranscriptionResponse> {
    try {
      const messages: ChatCompletionMessageParam[] = [
        { role: "system", content: this.systemPrompt },
        { 
          role: "user", 
          content: `Process this meeting transcription and identify the speaker. If speaker context is provided, use it to maintain consistency.

          Audio text: "${audioText}"
          Speaker context: ${speakerContext || "New speaker"}

          Respond with JSON in this format:
          {
            "text": "cleaned and formatted transcription",
            "speakerName": "Speaker Full Name",
            "speakerInitials": "XX", 
            "speakerColor": "css-color-class"
          }` 
        }
      ];

      const response = await openai.chat.completions.create({
        model: "gpt-5",
        messages,
        response_format: { type: "json_object" },
        temperature: 0.3,
      });

      const result = JSON.parse(response.choices[0].message.content || "{}");

      // Update conversation history
      this.updateConversationHistory(meetingId, messages, response.choices[0].message.content || "");

      return {
        text: result.text || audioText,
        speakerName: result.speakerName || "Unknown Speaker",
        speakerInitials: result.speakerInitials || "US",
        speakerColor: result.speakerColor || "bg-gray-500"
      };
    } catch (error) {
      console.error("OpenAI transcription processing error:", error);
      throw new Error("Failed to process transcription: " + (error as Error).message);
    }
  }

  async extractActionItems(meetingId: string, transcriptText: string): Promise<ActionItemResponse[]> {
    try {
      const context = this.getConversationContext(meetingId);

      const messages: ChatCompletionMessageParam[] = [
        { role: "system", content: this.systemPrompt },
        ...context,
        {
          role: "user",
          content: `Analyze this meeting transcript and extract action items relevant to newsletter publishing and content creation.

          Transcript: "${transcriptText}"

          Focus on identifying:
          - Content creation tasks
          - Editorial deadlines
          - Campaign planning items
          - Tool integrations
          - Follow-up meetings
          - Performance analysis tasks

          Respond with JSON in this format:
          {
            "actionItems": [
              {
                "title": "Brief action title",
                "description": "Detailed description",
                "assignedTo": "Person name if mentioned",
                "dueDate": "Timeframe if mentioned"
              }
            ]
          }`
        }
      ];

      const response = await openai.chat.completions.create({
        model: "gpt-5",
        messages,
        response_format: { type: "json_object" },
        temperature: 0.2,
      });

      const result = JSON.parse(response.choices[0].message.content || "{}");

      this.updateConversationHistory(meetingId, messages, response.choices[0].message.content || "");

      return result.actionItems || [];
    } catch (error) {
      console.error("OpenAI action item extraction error:", error);
      throw new Error("Failed to extract action items: " + (error as Error).message);
    }
  }

  async generateMeetingInsights(meetingId: string, fullTranscript: string): Promise<InsightResponse> {
    try {
      const context = this.getConversationContext(meetingId);

      const messages: ChatCompletionMessageParam[] = [
        { role: "system", content: this.systemPrompt },
        ...context,
        {
          role: "user",
          content: `Analyze this complete meeting transcript and provide insights for newsletter publishers.

          Full transcript: "${fullTranscript}"

          Generate comprehensive insights including:
          - Key discussion topics
          - Overall sentiment and team dynamics
          - Strategic next steps
          - Summary of decisions made

          Respond with JSON in this format:
          {
            "keyTopics": ["topic1", "topic2", "topic3"],
            "sentiment": "positive|neutral|negative",
            "sentimentScore": "descriptive explanation",
            "summary": "3-4 sentence meeting summary",
            "nextSteps": "Key next steps and recommendations"
          }`
        }
      ];

      const response = await openai.chat.completions.create({
        model: "gpt-5",
        messages,
        response_format: { type: "json_object" },
        temperature: 0.4,
      });

      const result = JSON.parse(response.choices[0].message.content || "{}");

      this.updateConversationHistory(meetingId, messages, response.choices[0].message.content || "");

      return {
        keyTopics: result.keyTopics || [],
        sentiment: result.sentiment || "neutral",
        sentimentScore: result.sentimentScore || "Neutral meeting tone",
        summary: result.summary || "Meeting discussion completed",
        nextSteps: result.nextSteps || "Follow up on discussed items"
      };
    } catch (error) {
      console.error("OpenAI insights generation error:", error);
      throw new Error("Failed to generate meeting insights: " + (error as Error).message);
    }
  }

  async streamChatResponse(meetingId: string, userMessage: string): Promise<AsyncIterableIterator<string>> {
    const context = this.getConversationContext(meetingId);

    const messages: ChatCompletionMessageParam[] = [
      { role: "system", content: this.systemPrompt },
      ...context,
      { role: "user", content: userMessage }
    ];

    const stream = await openai.chat.completions.create({
      model: "gpt-5",
      messages,
      stream: true,
      temperature: 0.7,
    });

    return this.processStreamResponse(stream, meetingId, messages);
  }

  private async *processStreamResponse(
    stream: AsyncIterable<OpenAI.Chat.Completions.ChatCompletionChunk>,
    meetingId: string,
    messages: ChatCompletionMessageParam[]
  ): AsyncIterableIterator<string> {
    let fullResponse = "";

    for await (const chunk of stream) {
      const content = chunk.choices[0]?.delta?.content || "";
      if (content) {
        fullResponse += content;
        yield content;
      }
    }

    // Update conversation history with complete response
    this.updateConversationHistory(meetingId, messages, fullResponse);
  }

  private getConversationContext(meetingId: string): ChatCompletionMessageParam[] {
    const history = this.conversationHistory.get(meetingId) || [];
    // Keep last 10 exchanges to maintain context without hitting token limits
    return history.slice(-20);
  }

  private updateConversationHistory(
    meetingId: string, 
    messages: ChatCompletionMessageParam[], 
    assistantResponse: string
  ): void {
    let history = this.conversationHistory.get(meetingId) || [];

    // Add user message and assistant response
    const userMessage = messages[messages.length - 1];
    if (userMessage.role === "user") {
      history.push(userMessage);
      history.push({ role: "assistant", content: assistantResponse });
    }

    // Maintain context window
    if (history.length > 40) {
      history = history.slice(-30);
    }

    this.conversationHistory.set(meetingId, history);
  }

  clearConversationHistory(meetingId: string): void {
    this.conversationHistory.delete(meetingId);
  }
}

export const openaiService = new OpenAIService();

server/services/transcription.ts.content:
import { storage } from "../storage";
import { openaiService } from "./openai";
import type { WSMessage, InsertTranscription, InsertActionItem } from "@shared/schema";

export class TranscriptionService {
  private activeMeetings: Map<string, {
    transcriptionBuffer: string;
    lastActionItemCheck: number;
    speakerMap: Map<string, { name: string; initials: string; color: string }>;
  }> = new Map();

  async processAudioStream(
    meetingId: string, 
    audioText: string, 
    wsCallback: (message: WSMessage) => void
  ): Promise<void> {
    try {
      // Initialize meeting context if not exists
      if (!this.activeMeetings.has(meetingId)) {
        this.activeMeetings.set(meetingId, {
          transcriptionBuffer: "",
          lastActionItemCheck: Date.now(),
          speakerMap: new Map()
        });
      }

      const meetingContext = this.activeMeetings.get(meetingId)!;

      // Process transcription with OpenAI
      const transcriptionResult = await openaiService.processTranscription(
        meetingId, 
        audioText,
        this.getSpeakerContext(meetingContext.speakerMap)
      );

      // Update speaker map
      meetingContext.speakerMap.set(transcriptionResult.speakerName, {
        name: transcriptionResult.speakerName,
        initials: transcriptionResult.speakerInitials,
        color: transcriptionResult.speakerColor
      });

      // Store transcription
      const transcription = await storage.createTranscription({
        meetingId,
        speakerName: transcriptionResult.speakerName,
        speakerInitials: transcriptionResult.speakerInitials,
        speakerColor: transcriptionResult.speakerColor,
        content: transcriptionResult.text,
        isStreaming: false
      });

      // Send real-time transcription update
      wsCallback({
        type: "transcription",
        data: {
          meetingId,
          speakerName: transcription.speakerName,
          speakerInitials: transcription.speakerInitials,
          speakerColor: transcription.speakerColor,
          content: transcription.content,
          isStreaming: false
        }
      });

      // Update transcription buffer
      meetingContext.transcriptionBuffer += ` ${transcriptionResult.text}`;

      // Check for action items every 30 seconds or 500 characters
      const shouldCheckActionItems = 
        Date.now() - meetingContext.lastActionItemCheck > 30000 ||
        meetingContext.transcriptionBuffer.length > 500;

      if (shouldCheckActionItems) {
        await this.extractActionItems(meetingId, meetingContext.transcriptionBuffer, wsCallback);
        meetingContext.lastActionItemCheck = Date.now();
      }

      // Update meeting stats
      await this.updateMeetingStats(meetingId, wsCallback);

    } catch (error) {
      console.error("Transcription processing error:", error);
      wsCallback({
        type: "error",
        data: {
          message: "Failed to process transcription",
          code: "TRANSCRIPTION_ERROR"
        }
      });
    }
  }

  async processStreamingText(
    meetingId: string,
    partialText: string,
    speakerInfo: { name: string; initials: string; color: string },
    wsCallback: (message: WSMessage) => void
  ): Promise<void> {
    try {
      // Send streaming update
      wsCallback({
        type: "transcription",
        data: {
          meetingId,
          speakerName: speakerInfo.name,
          speakerInitials: speakerInfo.initials,
          speakerColor: speakerInfo.color,
          content: partialText,
          isStreaming: true
        }
      });
    } catch (error) {
      console.error("Streaming text error:", error);
    }
  }

  private async extractActionItems(
    meetingId: string, 
    transcriptText: string, 
    wsCallback: (message: WSMessage) => void
  ): Promise<void> {
    try {
      const actionItems = await openaiService.extractActionItems(meetingId, transcriptText);

      for (const item of actionItems) {
        // Store action item
        const actionItem = await storage.createActionItem({
          meetingId,
          title: item.title,
          description: item.description,
          assignedTo: item.assignedTo,
          dueDate: item.dueDate,
          status: "pending",
          isCompleted: false
        });

        // Send real-time action item update
        wsCallback({
          type: "action_item",
          data: {
            meetingId: actionItem.meetingId,
            title: actionItem.title,
            description: actionItem.description || "",
            assignedTo: actionItem.assignedTo || "",
            dueDate: actionItem.dueDate || "",
            status: actionItem.status || "pending",
            isCompleted: actionItem.isCompleted || false
          }
        });
      }
    } catch (error) {
      console.error("Action item extraction error:", error);
    }
  }

  private async updateMeetingStats(
    meetingId: string, 
    wsCallback: (message: WSMessage) => void
  ): Promise<void> {
    try {
      const meeting = await storage.getMeeting(meetingId);
      if (!meeting) return;

      const transcriptions = await storage.getTranscriptionsByMeeting(meetingId);
      const uniqueSpeakers = new Set(transcriptions.map(t => t.speakerName)).size;

      const duration = meeting.startTime 
        ? Math.floor((Date.now() - meeting.startTime.getTime()) / 1000)
        : 0;

      // Update meeting record
      await storage.updateMeeting(meetingId, {
        duration,
        speakerCount: uniqueSpeakers
      });

      // Send meeting status update
      wsCallback({
        type: "meeting_status",
        data: {
          meetingId,
          status: meeting.status as "active" | "paused" | "completed",
          duration,
          speakerCount: uniqueSpeakers
        }
      });
    } catch (error) {
      console.error("Meeting stats update error:", error);
    }
  }

  private getSpeakerContext(speakerMap: Map<string, any>): string {
    const speakers = Array.from(speakerMap.values());
    if (speakers.length === 0) return "No previous speakers";

    return `Known speakers: ${speakers.map(s => `${s.name} (${s.initials})`).join(", ")}`;
  }

  async finalizeMeeting(
    meetingId: string, 
    wsCallback: (message: WSMessage) => void
  ): Promise<void> {
    try {
      const transcriptions = await storage.getTranscriptionsByMeeting(meetingId);
      const fullTranscript = transcriptions.map(t => `${t.speakerName}: ${t.content}`).join("\n");

      // Generate comprehensive insights
      const insights = await openaiService.generateMeetingInsights(meetingId, fullTranscript);

      // Store insights
      await storage.createMeetingInsight({
        meetingId,
        keyTopics: insights.keyTopics,
        sentiment: insights.sentiment,
        sentimentScore: insights.sentimentScore,
        summary: insights.summary,
        nextSteps: insights.nextSteps
      });

      // Update meeting status
      await storage.updateMeeting(meetingId, {
        status: "completed",
        endTime: new Date()
      });

      // Clean up active meeting context
      this.activeMeetings.delete(meetingId);
      openaiService.clearConversationHistory(meetingId);

      // Send final meeting status
      wsCallback({
        type: "meeting_status",
        data: {
          meetingId,
          status: "completed",
          duration: 0,
          speakerCount: 0
        }
      });

    } catch (error) {
      console.error("Meeting finalization error:", error);
      wsCallback({
        type: "error",
        data: {
          message: "Failed to finalize meeting",
          code: "FINALIZATION_ERROR"
        }
      });
    }
  }

  getActiveMeetingCount(): number {
    return this.activeMeetings.size;
  }
}

export const transcriptionService = new TranscriptionService();

